import React, {Component} from "react";

export class WordCounter extends Component {
	state = {
		word: "",
		count: 0,
	};

	handleChange = (event) => {
		event.preventDefault();
	};

	render() {
		return (
			<div>
				<form onSubmit={this.handleSubmit}>
					<label>Word:</label>
					<input
						type="text"
						onChange={this.handleChange}
						value={this.state.word}
					/>

					<input type="submit" />
				</form>
			</div>
		);
	}
}

export default WordCounter;
