import React, {Component} from "react";
import {getUser} from "../redux/actions/userActions";
import {connect} from "react-redux";
import {getToken} from "../services/local_storage";
import {Redirect} from "react-router-dom";

export class Profile extends Component {
	componentDidMount() {
		console.log(this.props);
		this.props.getUser();
	}

	renderWorkoutSessions = () => {
		return this.props.user.workout_sessions.map((ws) => (
			<p>
				{ws.name} on {ws.created_at.toLocaleString()}
			</p>
		));
	};
	render() {
		return (
			<div>
				{getToken() ? <Redirect to="/login" /> : null}
				{console.log(this.props.user)}
				<h1>{this.props.user.name}'s Profile! </h1>
				{this.renderWorkoutSessions()}
			</div>
		);
	}
}

const mapStateToProps = (state) => {
	const {user, workout_sessions} = state;
	return {
		user,
		workout_sessions,
	};
};

const mapDispatchToProps = (dispatch) => {
	return {
		getUser: () => dispatch(getUser()),
	};
};

export default connect(mapStateToProps, mapDispatchToProps)(Profile);
