import React from "react";
import {useSelector, useDispatch} from "react-redux";
import WorkoutModal from "./WorkoutModal";

const ShowWorkouts = () => {
	const workouts = useSelector((state) => state.workouts);
	const dispatch = useDispatch();

	// const handleClick = (e) => {
	// 	return console.log("clicked");
	// };

	const renderWorkouts = workouts.map((workout) => (
		<h3 onClick={() => dispatch({type: "DELETE_WORKOUT", payload: workout})}>
			{workout.name} on {new Date(workout.created_at).toLocaleDateString()}
			{/* <button onClick={handleClick}>More</button> */}
		</h3>
	));

	return <div>{renderWorkouts}</div>;
};

export default ShowWorkouts;

//changes state for show workout so there is workout to be displayed
