import React from "react";
import {useSelector, useDispatch} from "react-redux";
import WorkoutModal from "./WorkoutModal";
import {deleteWorkout} from "../../services/api";

const ShowWorkouts = () => {
	const state = useSelector((state) => state);
	// const dispatch = useDispatch();

	const handleClick = (workout) => {
		console.log(state);
		dispatch({type: "DELETE_WORKOUT", payload: workout});

		deleteWorkout(state.user.id, workout);
	};

	const renderWorkouts = state.workouts.map((workout) => (
		<h3>
			{workout.name} on {new Date(workout.created_at).toLocaleDateString()}
			<button onClick={handleClick(workout)}>delete</button>
		</h3>
	));

	return (
		<div>
			{console.log(state)}
			{renderWorkouts}
		</div>
	);
};

export default ShowWorkouts;

//changes state for show workout so there is workout to be displayed
