import React, {Component} from "react";
import {profileRequest} from "../services/api";

export class Profile extends Component {
	state = {
		name: "",
		workout_sessions: [],
	};

	componentDidMount() {
		profileRequest().then((res) => {
			if (!res.error) {
				this.setState({name: res.name, workout_sessions: res.workout_sessions});
			}
		});
	}

	renderWorkoutSessions = () => {
		this.state.workout_sessions.map((ws) => <p>{ws.name}</p>);
	};
	render() {
		return (
			<div>
				{this.state.name}'s Profile!
				{this.renderWorkoutSessions()}
			</div>
		);
	}
}

export default Profile;
