import React, {Component} from "react";
import {profileRequest} from "../services/api";
import {getUser} from "../redux/actions/userActions";
import {connect} from "react-redux";

export class Profile extends Component {
	state = {
		name: "",
		workout_sessions: [],
	};

	componentDidMount() {
		profileRequest().then((res) => {
			if (!res.error) {
				this.setState({name: res.name, workout_sessions: res.workout_sessions});
			}
		});
	}

	renderWorkoutSessions = () => {
		return this.state.workout_sessions.map((ws) => (
			<p>
				{ws.name} on {ws.created_at}
			</p>
		));
	};
	render() {
		return (
			<div>
				<h1>{this.state.name}'s Profile! </h1>
			</div>
		);
	}
}

const mapStateToProps = (state) => {
	const {name, workout_sessions} = state;
	return {
		name,
		workout_sessions,
	};
};

const mapDispatchToProps = (dispatch) => {
	return getUser;
};

export default connect(mapStateToProps, mapDispatchToProps)(Profile);
